import { useCookies } from "react-cookie";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios';

function Profile(){
    const [cookies, setCookie] = useCookies();
    const navigate = useNavigate();
    const [blogs,setBlog] = useState([]);

    let id = window.location.pathname.split('/');
    id = id[id.length-1];

    useEffect(() => {
        console.log('yo');
        axios.post('http://localhost:4000/getBlogs', {
            id: id,
        }).then(response => {
            // Use the spread operator to concatenate the new data to the existing blogs
            setBlog(oldBlogs => [...oldBlogs, ...response.data]);
        }).catch(err => {
            console.log(err);
        });
    }, []);    

    useEffect(() => {
        if (cookies.username === undefined) {
            navigate('/');
        }
        axios.get('http://localhost:4000/authenticate', { headers: { "Authorization": `Bearer ${cookies.username}` } }).then(response => {
            console.log(response);
        }).catch(err => {
            console.log(err);
            navigate("/");
        })
    }, [cookies.username]);

    return (
        <div>
            {
                blogs.map(blog=>{
                    <h1>{blog}</h1>
                })
            }
        </div>
    );
}

export default Profile;
