import { useState } from "react";
import { useCookies } from 'react-cookie';
import { useNavigate } from "react-router-dom";
import axios from 'axios';

function Login() {
    const [username,setusername] = useState("");
    const [pwd,setPwd] = useState("");
    const [cookies,setCookies] = useCookies();
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post("http://localhost:4000/login",{
          username : username,
          password : pwd
        }).then((response)=>{
          setCookies("username",response['data']['token']);
          setCookies("name",response['data']['name']);
          navigate('/home');
        }).catch((err)=>{
          navigate('/');
        })
    }
    return (
      <div className="App">
       <form onSubmit={handleSubmit}>
        {/* <input name="Username" placeholder="Username" onChange={(e)=>{setusername(e.target.value)}}></input><br/><br/> */}
        <div class="form-group">
          <label>UserName</label>
          <input class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="UserName"/>
          <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <input name="Password" placeholder="Password" onChange={(e)=>{setPwd(e.target.value)}}></input><br/><br/>
        <button>Submit</button>
       </form>
      </div>
    );
  }

  export default Login;
